import{_ as e,o as n,c as a,Q as s}from"./chunks/framework.01af844e.js";const m=JSON.parse('{"title":"Container Environment","description":"","frontmatter":{},"headers":[],"relativePath":"golang/k8s/concept/container/container_env.md","filePath":"golang/k8s/concept/container/container_env.md","lastUpdated":1694534070000}'),o={name:"golang/k8s/concept/container/container_env.md"},t=s(`<h1 id="container-environment" tabindex="-1">Container Environment <a class="header-anchor" href="#container-environment" aria-label="Permalink to &quot;Container Environment&quot;">​</a></h1><p>This page describes the resources available to Containers in the Container environment.</p><h2 id="container-environment-1" tabindex="-1">Container environment <a class="header-anchor" href="#container-environment-1" aria-label="Permalink to &quot;Container environment&quot;">​</a></h2><p>The Kubernetes Container environment provides several important resources to Containers:</p><ul><li>A filesystem, which is a combination of an <a href="https://kubernetes.io/docs/concepts/containers/images/" target="_blank" rel="noreferrer">image</a> and one or more <a href="https://kubernetes.io/docs/concepts/storage/volumes/" target="_blank" rel="noreferrer">volumes</a>.</li><li>Information about the Container itself.</li><li>Information about other objects in the cluster.</li></ul><h3 id="container-information" tabindex="-1">Container information <a class="header-anchor" href="#container-information" aria-label="Permalink to &quot;Container information&quot;">​</a></h3><p>The hostname of a Container is the name of the Pod in which the Container is running. It is available through the <code>hostname</code> command or the <code>gethostname</code> function call in libc.</p><p>The Pod name and namespace are available as environment variables through the <a href="https://kubernetes.io/docs/tasks/inject-data-application/downward-api-volume-expose-pod-information/" target="_blank" rel="noreferrer">downward API</a>.</p><p>User defined environment variables from the Pod definition are also available to the Container, as are any environment variables specified statically in the container image.</p><h3 id="cluster-information" tabindex="-1">Cluster information <a class="header-anchor" href="#cluster-information" aria-label="Permalink to &quot;Cluster information&quot;">​</a></h3><p>A list of all services that were running when a Container was created is available to that Container as environment variables. This list is limited to services within the same namespace as the new Container&#39;s Pod and Kubernetes control plane services.</p><p>For a service named foo that maps to a Container named bar, the following variables are defined:</p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#E1E4E8;">FOO_SERVICE_HOST</span><span style="color:#F97583;">=&lt;</span><span style="color:#9ECBFF;">the</span><span style="color:#E1E4E8;"> </span><span style="color:#B392F0;">host</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">the</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">service</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">is</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">running</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">o</span><span style="color:#E1E4E8;">n</span><span style="color:#F97583;">&gt;</span></span>
<span class="line"><span style="color:#E1E4E8;">FOO_SERVICE_PORT</span><span style="color:#F97583;">=&lt;</span><span style="color:#9ECBFF;">the</span><span style="color:#E1E4E8;"> </span><span style="color:#B392F0;">port</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">the</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">service</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">is</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">running</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">o</span><span style="color:#E1E4E8;">n</span><span style="color:#F97583;">&gt;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292E;">FOO_SERVICE_HOST</span><span style="color:#D73A49;">=&lt;</span><span style="color:#032F62;">the</span><span style="color:#24292E;"> </span><span style="color:#6F42C1;">host</span><span style="color:#24292E;"> </span><span style="color:#032F62;">the</span><span style="color:#24292E;"> </span><span style="color:#032F62;">service</span><span style="color:#24292E;"> </span><span style="color:#032F62;">is</span><span style="color:#24292E;"> </span><span style="color:#032F62;">running</span><span style="color:#24292E;"> </span><span style="color:#032F62;">o</span><span style="color:#24292E;">n</span><span style="color:#D73A49;">&gt;</span></span>
<span class="line"><span style="color:#24292E;">FOO_SERVICE_PORT</span><span style="color:#D73A49;">=&lt;</span><span style="color:#032F62;">the</span><span style="color:#24292E;"> </span><span style="color:#6F42C1;">port</span><span style="color:#24292E;"> </span><span style="color:#032F62;">the</span><span style="color:#24292E;"> </span><span style="color:#032F62;">service</span><span style="color:#24292E;"> </span><span style="color:#032F62;">is</span><span style="color:#24292E;"> </span><span style="color:#032F62;">running</span><span style="color:#24292E;"> </span><span style="color:#032F62;">o</span><span style="color:#24292E;">n</span><span style="color:#D73A49;">&gt;</span></span></code></pre></div><p>Services have dedicated IP addresses and are available to the Container via DNS, if <a href="https://releases.k8s.io/v1.28.1/cluster/addons/dns/" target="_blank" rel="noreferrer">DNS addon</a> is enabled.</p><h2 id="what-s-next" tabindex="-1">What&#39;s next <a class="header-anchor" href="#what-s-next" aria-label="Permalink to &quot;What&#39;s next&quot;">​</a></h2><ul><li>Learn more about <a href="https://kubernetes.io/docs/concepts/containers/container-lifecycle-hooks/" target="_blank" rel="noreferrer">Container lifecycle hooks</a>.</li><li>Get hands-on experience <a href="https://kubernetes.io/docs/tasks/configure-pod-container/attach-handler-lifecycle-event/" target="_blank" rel="noreferrer">attaching handlers to Container lifecycle events</a>.</li></ul>`,16),r=[t];function l(i,p,c,h,d,E){return n(),a("div",null,r)}const v=e(o,[["render",l]]);export{m as __pageData,v as default};
