import{_ as p,H as s,o as c,c as u,L as t,w as a,a as e,V as d,k as o}from"./chunks/framework.48c56699.js";const h="/allure/surefire_report_folder.png",F=JSON.parse('{"title":"Allure Framework","description":"","frontmatter":{},"headers":[],"relativePath":"python/allure/get_start.md","filePath":"python/allure/get_start.md","lastUpdated":1692437254000}'),m={name:"python/allure/get_start.md"},f=d(`<h1 id="allure-framework" tabindex="-1">Allure Framework <a class="header-anchor" href="#allure-framework" aria-label="Permalink to &quot;Allure Framework&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">Tip</p><p>allure document version: <code>2.23.1</code>. the lastest version is only maintained.</p><p>lastest updated: <em>2023/08/14</em></p></div><p>Allure Framework is a flexible lightweight multi-language test report tool that not only shows a very concise representation of what have been tested in a neat web report form, but allows everyone participating in the development process to extract maximum of useful information from everyday execution of tests.</p><p>From the dev/qa perspective Allure reports shorten common defect lifecycle: test failures can be divided on bugs and broken tests, also logs, steps, fixtures, attachments, timings, history and integrations with TMS and bug-tracking systems can be configured, so the responsible developers and testers will have all information at hand.</p><p>From the managers perspective Allure provides a clear &#39;big picture&#39; of what features have been covered, where defects are clustered, how the timeline of execution looks like and many other convenient things. Modularity and extensibility of Allure guarantees that you will always be able to fine-tune something to make Allure suit you better.</p><h2 id="about" tabindex="-1">About <a class="header-anchor" href="#about" aria-label="Permalink to &quot;About&quot;">​</a></h2><h3 id="copyright" tabindex="-1">Copyright <a class="header-anchor" href="#copyright" aria-label="Permalink to &quot;Copyright&quot;">​</a></h3><p>The Allure reference guide is available as HTML documents. The latest copy is available at <a href="https://docs.qameta.io/allure/" target="_blank" rel="noreferrer">https://docs.qameta.io/allure/</a></p><p>Copies of this document may be made for your own use and for distribution to others, provided that you do not charge any fee for such copies and further provided that each copy contains this Copyright Notice, whether distributed in print or electronically.</p><h3 id="get-help" tabindex="-1">Get Help <a class="header-anchor" href="#get-help" aria-label="Permalink to &quot;Get Help&quot;">​</a></h3><p>There are several places to get help:</p><ul><li><p>Contact the community on <a href="https://gitter.im/allure-framework/allure-core" target="_blank" rel="noreferrer">Gitter</a>. We also have a <a href="https://gitter.im/allure-framework/allure-ru" target="_blank" rel="noreferrer">Russian-speaking room</a>.</p></li><li><p>Ask a question on <a href="https://stackoverflow.com/questions/ask?tags=allure" target="_blank" rel="noreferrer">Stack Overflow</a> or <a href="https://ru.stackoverflow.com/questions/ask?tags=allure" target="_blank" rel="noreferrer">Stack Overflow in Russian</a>.</p></li><li><p>Report bugs in <a href="https://github.com/allure-framework/allure2/issues/new?" target="_blank" rel="noreferrer">GitHub issues</a>.</p></li></ul><h3 id="how-to-proceed" tabindex="-1">How to Proceed <a class="header-anchor" href="#how-to-proceed" aria-label="Permalink to &quot;How to Proceed&quot;">​</a></h3><ul><li><p>Open the <a href="http://demo.qameta.io/allure/latest/" target="_blank" rel="noreferrer">demo version</a> to see what an Allure report looks like.</p></li><li><p>Go to <a href="https://docs.qameta.io/allure/latest/#_get_started" target="_blank" rel="noreferrer">Get started</a> to build a report for an existing project.</p></li><li><p>Learn more about <a href="https://docs.qameta.io/allure/latest/#_report_structure" target="_blank" rel="noreferrer">report structure and features</a>.</p></li><li><p>Integrate your favorite testing framework with Allure. Supported frameworks are grouped by language: <code>Java</code>, <code>Python</code>, <code>JavaScript</code>, <code>Ruby</code>, <code>Groovy</code>, <code>PHP</code>, <code>.Net</code>, and <code>Scala</code>.</p></li></ul><h2 id="get-started" tabindex="-1">Get Started <a class="header-anchor" href="#get-started" aria-label="Permalink to &quot;Get Started&quot;">​</a></h2><p>To generate your first report you will need to go through just a few simple steps:</p><ul><li><p>Downloading and installing Allure commandline application suitable for your environment.</p></li><li><p>Locating test execution data that you have to build a report on.</p></li></ul><h3 id="installing-a-commandline" tabindex="-1">Installing a commandline <a class="header-anchor" href="#installing-a-commandline" aria-label="Permalink to &quot;Installing a commandline&quot;">​</a></h3><p>Several options for Allure installation are currently supported:</p><h4 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h4><p>For debian-based repositories a PPA is provided:</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">apt-add-repository</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ppa:qameta/allure</span></span>
<span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">apt-get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">update</span><span style="color:#A6ACCD;"> </span></span>
<span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">apt-get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">allure</span></span></code></pre></div><h4 id="mac-os-x" tabindex="-1">Mac OS X <a class="header-anchor" href="#mac-os-x" aria-label="Permalink to &quot;Mac OS X&quot;">​</a></h4><p>For Mas OS, automated installation is available via <a href="https://brew.sh/" target="_blank" rel="noreferrer">Homebrew</a></p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">brew</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">allure</span></span></code></pre></div><h4 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h4><p>For Windows, Allure is available from the <a href="http://scoop.sh/" target="_blank" rel="noreferrer">Scoop</a> commandline-installer.</p><p>To install Allure, download and install Scoop and then execute in the Powershell:</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">scoop</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">allure</span></span></code></pre></div><p>Also Scoop is capable of updating Allure distribution installations. To do so navigate to the Scoop installation directory and execute</p><div class="language-bat"><button title="Copy Code" class="copy"></button><span class="lang">bat</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">\\bin\\checkver.ps1 allure -u</span></span></code></pre></div><p>This will check for newer versions of Allure, and update the manifest file. Then execute</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">scoop</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">update</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">allure</span></span></code></pre></div><p>to install a newer version. (<a href="https://github.com/lukesampson/scoop/wiki/App-Manifest-Autoupdate" target="_blank" rel="noreferrer">documentation</a>)</p><h4 id="manual-installation" tabindex="-1">Manual installation <a class="header-anchor" href="#manual-installation" aria-label="Permalink to &quot;Manual installation&quot;">​</a></h4><ol><li><p>Download the latest version as zip archive from <a href="https://repo.maven.apache.org/maven2/io/qameta/allure/allure-commandline/" target="_blank" rel="noreferrer">Maven Central</a>.</p></li><li><p>Unpack the archive to allure-commandline directory.</p></li><li><p>Navigate to <code>bin</code> directory.</p></li><li><p>Use <code>allure.bat</code> for Windows or <code>allure</code> for other Unix platforms.</p></li><li><p>Add <code>allure</code> to system <code>PATH</code>.</p></li></ol><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>To run commandline application, Java Runtime Environment must be installed.</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Older releases (⇐ <code>2.8.0</code>) are available on <a href="https://bintray.com/qameta/generic/allure2" target="_blank" rel="noreferrer">bintray</a>.</p></div><h4 id="check-the-installation" tabindex="-1">Check the installation <a class="header-anchor" href="#check-the-installation" aria-label="Permalink to &quot;Check the installation&quot;">​</a></h4><p>Execute <code>allure --version</code> in console to make sure that allure is now available:</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">$</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">allure</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--version</span></span>
<span class="line"><span style="color:#FFCB6B;">2.0.1</span></span></code></pre></div><h3 id="test-execution" tabindex="-1">Test execution <a class="header-anchor" href="#test-execution" aria-label="Permalink to &quot;Test execution&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>If you are using IDE to run tests locally it may ignore Allure configuration specified in build file (as IntelliJ IDEA does). In order to make it work consider using allure.properties file to configure Allure. Check out <a href="https://docs.qameta.io/allure-report/#_configuration" target="_blank" rel="noreferrer">configuration section</a> for more information.</p></div><p>Before building a report you need to run your tests to obtain some basic test report data. Typically it might be a junit-style xml report generated by nearly every popular test framework. For example, suppose you have test reports automatically created by surefire maven plugin stored in the <code>target/surefire-reports</code>:</p><p><img src="`+h+'" alt="surefire_report_folder"></p><p>2.3. Report generation This is already enough to see the Allure report in one command:</p><p>allure serve /home/path/to/project/target/surefire-reports/</p><p>Which generates a report in temporary folder from the data found in the provided path and then creates a local Jetty server instance, serves generated report and opens it in the default browser. It is possible to use a --profile option to enable some pre-configured allure setting. junit profile is enabled by default, you will learn more about profiles in the following section.</p><p>This would produce a report with a minimum of information extracted from the xml data that will lack nearly all of the advanced allure features but will allow you to get a nice visual representation of executed tests.</p><p>Report generated on xml data</p><ol><li>Report structure Once you’ve got the idea what the report does look like. You will probably want to get more data-rich reports. You might have to consider using one of the Allure adaptors for your testing framework, which will allow to collect much more information. Jump to the integrations section to learn more about integration with testing frameworks.</li></ol><p>Typical report consists of &#39;Overview&#39; tab, navigation bar, several tabs for different kinds of test data representation and test case pages for each individual test. Each Allure report is backed by a tree-like data structure, that represents a test execution process. Different tabs allow to switch between the views of the original data structure thus giving a different perspective. Note that all tree-like representations including Behaviors, Categories, xUnit and Packages support filtering and are sortable.</p><p>3.1. Overview page Entry point for every report would be the &#39;Overview&#39; page with dashboards and widgets:</p><p>Overview Overview page hosts several default widgets representing basic characteristics of your project and test environment.</p><p>Statistics - overall report statistics.</p><p>Launches - if this report represents several test launches, statistics per launch will be shown here.</p><p>Behaviors - information on results aggregated according to stories and features.</p><p>Executors - information on test executors that were used to run the tests.</p><p>History Trend - if tests accumulated some historical data, it’s trend will be calculated and shown on the graph.</p><p>Environment - information on test environment (see how to define environment).</p><p>Home page widgets are draggable and configurable. Also, Allure supports it’s own plugin system, so quite different widget layouts are possible.</p><p>Navigation bar is collapsible and enables you to switch into several of the basic results overview modes.</p><p>3.2. Categories Categories tab gives you the way to create custom defects classification to apply for test results.</p><p>Categories 3.3. Suites On the Suites tab a standard structural representation of executed tests, grouped by suites and classes can be found.</p><p>Suites 3.4. Graphs Graphs allow you to see different statistics collected from the test data: statuses breakdown or severity and duration diagrams.</p><p>Graphs 3.5. Timeline Timeline tab visualizes retrospective of tests execution, allure adaptors collect precise timings of tests, and here on this tab they are arranged accordingly to their sequential or parallel timing structure.</p><p>Timeline 3.6. Behaviors For Behavior-driven approach, this tab groups test results according to Epic, Feature and Story tags.</p><p>Behaviors 3.7. Packages Packages tab represents a tree-like layout of test results, grouped by different packages.</p><p>Packages 3.8. Test case page From some of the results overview pages described above you can go to the test case page after clicking on the individual tests. This page will typically contain a lot of individual data related to the test case: steps executed during the test, timings, attachments, test categorization labels, descriptions and links.</p><p>Test result page 4. Features This section describes the main features of Allure. For example, you can group your tests by stories or features, attach files, and distribute assertions over a set of custom steps, among other features. All features are supported by Java test frameworks, so we only provide Java examples here. For details on how a particular adapter works with the test framework of your choice, refer to the adapter guide.</p><p>4.1. Flaky tests In real life not all of your tests are stable and always green or always red. A test might start to &quot;blink&quot; i.e. it fails from time-to-time without any obvious reason. You could disable such a test, that is a trivial solution. However what if you do not want to do that? Say you would like to get more details on possible reasons or the test is so critical that even being flaky it provides helpful information? You have an option now to mark such tests in a special way, so the resulting report will clearly show them as unstable:</p><p>@Flaky public void aTestWhichFailsFromTimeToTime { ... } Here is what you get in the report if such a test failed:</p><p>A failed test marked as flaky you can mark a whole test class as flaky as well. 4.2. Environment To add information to Environment widget just create environment.properties (or environment.xml) file to allure-results directory before report generation.</p><p>environment.properties Browser=Chrome Browser.Version=63.0 Stand=Production environment.xml</p>',74),g=o("p",null,"Product defects (failed tests)",-1),b=o("p",null,"Test defects (broken tests)",-1),y=o("p",null,"To create custom defects classification add categories.json file to allure-results directory before report generation.",-1),v=o("p",null,[e('categories.json [ { "name": "Ignored tests", "matchedStatuses": ["skipped"] }, { "name": "Infrastructure problems", "matchedStatuses": ["broken", "failed"], "messageRegex": ".'),o("em",null,"bye-bye."),e('" }, { "name": "Outdated tests", "matchedStatuses": ["broken"], "traceRegex": ".'),o("em",null,"FileNotFoundException."),e('" }, { "name": "Product defects", "matchedStatuses": ["failed"] }, { "name": "Test defects", "matchedStatuses": ["broken"] } ] (mandatory) category name (optional) list of suitable test statuses. Default ["failed", "broken", "passed", "skipped", "unknown"] (optional) regex pattern to check test error message. Default ".'),o("em",null,'" (optional) regex pattern to check stack trace. Default ".'),e('" Test result falls into the category if its status is in the list and both error message and stack trace match the pattern.')],-1),w=o("p",null,"categories.json file can be stored in test resources directory in case of using allure-maven or allure-gradle plugins.",-1);function k(C,_,A,x,T,q){const r=s("key"),l=s("value"),n=s("parameter"),i=s("environment");return c(),u("div",null,[f,t(i,null,{default:a(()=>[t(n,null,{default:a(()=>[t(r,null,{default:a(()=>[e("Browser")]),_:1}),t(l,null,{default:a(()=>[e("Chrome")]),_:1})]),_:1}),t(n,null,{default:a(()=>[t(r,null,{default:a(()=>[e("Browser.Version")]),_:1}),t(l,null,{default:a(()=>[e("63.0")]),_:1})]),_:1}),t(n,null,{default:a(()=>[t(r,null,{default:a(()=>[e("Stand")]),_:1}),t(l,null,{default:a(()=>[e("Production")]),_:1})]),_:1})]),_:1}),e(" 4.3. Categories There are two categories of defects by default: "),g,b,y,v,w])}const P=p(m,[["render",k]]);export{F as __pageData,P as default};
