<span class="line"><span style="color:#E1E4E8;">    app.wsgi_app, </span><span style="color:#FFAB70;">x_for</span><span style="color:#F97583;">=</span><span style="color:#79B8FF;">1</span><span style="color:#E1E4E8;">, </span><span style="color:#FFAB70;">x_proto</span><span style="color:#F97583;">=</span><span style="color:#79B8FF;">1</span><span style="color:#E1E4E8;">, </span><span style="color:#FFAB70;">x_host</span><span style="color:#F97583;">=</span><span style="color:#79B8FF;">1</span><span style="color:#E1E4E8;">, </span><span style="color:#FFAB70;">x_prefix</span><span style="color:#F97583;">=</span><span style="color:#79B8FF;">1</span></span>
