import{_ as s,o as e,c as o,X as a}from"./chunks/framework.b5656a4e.js";const g=JSON.parse('{"title":"How to set up bash completion","description":"","frontmatter":{},"headers":[],"relativePath":"python/pytest/how_to_guides/bash_completion.md","filePath":"python/pytest/how_to_guides/bash_completion.md","lastUpdated":1692807718000}'),t={name:"python/pytest/how_to_guides/bash_completion.md"},l=a('<h1 id="how-to-set-up-bash-completion" tabindex="-1">How to set up bash completion <a class="header-anchor" href="#how-to-set-up-bash-completion" aria-label="Permalink to &quot;How to set up bash completion {#how-to-set-up-bash-completion}&quot;">â€‹</a></h1><p>When using bash as your shell, <code>pytest</code> can use argcomplete (<a href="https://kislyuk.github.io/argcomplete/" target="_blank" rel="noreferrer">https://kislyuk.github.io/argcomplete/</a>) for auto-completion. For this <code>argcomplete</code> needs to be installed and enabled.</p><p>Install argcomplete using:</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pip</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">argcomplete&gt;=0.5.7</span><span style="color:#89DDFF;">&#39;</span></span></code></pre></div><p>For global activation of all argcomplete enabled python applications run:</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">activate-global-python-argcomplete</span></span></code></pre></div><p>For permanent (but not global) <code>pytest</code> activation, use:</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">register-python-argcomplete</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pytest</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&gt;&gt;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">~/.bashrc</span></span></code></pre></div><p>For one-time activation of <code>argcomplete</code> for <code>pytest</code> only, use:</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">eval</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;$(</span><span style="color:#FFCB6B;">register-python-argcomplete</span><span style="color:#C3E88D;"> pytest</span><span style="color:#89DDFF;">)&quot;</span></span></code></pre></div>',10),p=[l];function n(c,r,i,h,d,y){return e(),o("div",null,p)}const m=s(t,[["render",n]]);export{g as __pageData,m as default};
